function Contact() {


  function sendEmail(){
    console.log(window)
    const Email=window.Email
    Email.send({
      Host : "smtp.gmail.com",
      Username : "eternalcloudservices@gmail.com",
      Password : "Prizm$123",
      To : 'eternalcloudservices@gmail.com',
      From : document.getElementById("email").value,
      Subject : "Enquiry to Eternal Cloud Services",
      Body : document.getElementById("message").value
  }).then(
    message => alert(message)
  )
  }  

  return (

    <div className="container contact" method = "POST">
      <form onSubmit={sendEmail} >
        <h2>CONTACT</h2>
        <input type="text" id="name" placeholder="Name" name="name" required></input>
        <input type="email" id="email" placeholder="Email Id" email="email" required></input>
        <input type="text" id="phone" placeholder="Phone no." required></input>
          <textarea id="message" rows="4" name="message" placeholder="How can we help you?"></textarea>
          <button type ="submit">Send</button>

      </form>


    </div>

  );
}

export default Contact;
